# 发送钱包地址
sender_address = cc.WALLET_ADDRESS
sender_address = web3.toChecksumAddress(sender_address)

balance = web3.eth.get_balance(sender_address)

humanReadable = web3.fromWei(balance, 'ether')

tokenToBuy = web3.toChecksumAddress("0x156ab3346823b651294766e23e6cf87254d68962")	#要购买的合约
spend = web3.toChecksumAddress("0xe9e7CEA3DedcA5984780Bafc599bD69ADd087D56") #busd contract

panRouterContractAddress = web3.toChecksumAddress(panRouterContractAddress)
contract = web3.eth.contract(address=panRouterContractAddress, abi=panabi)

nonce = web3.eth.get_transaction_count(sender_address)

start = time.time()

# 兑换合约 spend 兑换成 tokenToBuy
pancakeswap2_txn = contract.functions.swapExactTokensForTokens(
    1,  # 输入的数量
    0,  # 最少输出数量
    [spend, tokenToBuy],
    sender_address,
    (int(time.time()) + 10000)
).buildTransaction({
    'from': sender_address,
    'gas': 160000,
    'gasPrice': web3.toWei('5', 'gwei'),
    'nonce': nonce,
})
my_private_key = cc.PRIVATE_KEY
signed_txn = web3.eth.account.sign_transaction(pancakeswap2_txn, private_key= my_private_key)
tx_token = web3.eth.send_raw_transaction(signed_txn.rawTransaction)
print(web3.toHex(tx_token))
